nums=[]
#for i in range(start,end,jump)
for i in range(21):
    nums.append(i)

print("-------------------------------")
for i in range(1,21):
    print(nums[i])

print("-------------------------------")
for i in range(1,20):
    if i==3:
        break
    else: print(i)

print("-------------------------------")
for i in range(1,5):
    if i==3:
        continue
    else: print(i)

